package com.example.well.luochen.utils;import java.io.UnsupportedEncodingException;import java.math.BigInteger;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;public class MD5 {	public static String encodeMD5String(String str) {		return encode(str, "MD5");	}	private static String encode(String str, String method) {		MessageDigest md = null;		String dstr = null;		try {			md = MessageDigest.getInstance(method);			md.update(str.getBytes());			dstr = new BigInteger(1, md.digest()).toString(16);		} catch (Exception e) {			e.printStackTrace();		}		return dstr;	}	public static String md5(String string) {		byte[] hash;		try {			hash = MessageDigest.getInstance("MD5").digest(					string.getBytes("UTF-8"));		} catch (NoSuchAlgorithmException e) {			throw new RuntimeException("Huh, MD5 should be supported?", e);		} catch (UnsupportedEncodingException e) {			throw new RuntimeException("Huh, UTF-8 should be supported?", e);		}		StringBuilder hex = new StringBuilder(hash.length * 2);		for (byte b : hash) {			if ((b & 0xFF) < 0x10)				hex.append("0");			hex.append(Integer.toHexString(b & 0xFF));		}		return hex.toString();	}}